CREATE TABLE brand(
   ID INT PRIMARY KEY     NOT NULL,
   title            CHAR(50),
   subTitle            CHAR(50),
   description        TEXT
);

INSERT INTO brand (id,title,subTitle,`description`) VALUES ( 1,"Coca Cola Great Britain "," Founded in 1886 Dr John S Pemberton ","The Coca Cola Company is the world's leading manufacturer, marketer and distributor of non-alcoholic beverage concentrates and syrups, and produces nearly 400 brands.");
INSERT INTO brand (id,title,subTitle,`description`) VALUES ( 2,"Coca Cola "," New York Harbour, 1886 ","It was 1886, John Pemberton, an Atlanta pharmacist, was inspired by simple curiosity. One afternoon, he stirred up a fragrant, caramel-coloured liquid and, when it was done, the mixture was combined with carbonated water and sampled by customers who all agreed - this new drink was something special!");
INSERT INTO brand (id,title,subTitle,`description`) VALUES ( 3,"Sprite "," West Germany, 1959 ","First introduced in 1961, crisp, refreshing, clean-tasting Sprite is now the world's leading lemon and lime flavoured soft drink and is sold in more than 190 different countries. Sprite Zero, part of our no sugar Zero range, offers the delicious lemon lime taste of Sprite without the sugar or calories.");
INSERT INTO brand (id,title,subTitle,`description`) VALUES ( 4,"Dr Pepper "," Texas, 1885 ","Dr Pepper's unique, sparkling blend of 23 fruit flavours has been around for well over a century and it's still the same, with that distinctive flavour you just can't quite put your tongue on. It was created by Texas pharmacist Charles Alderton in 1885. The distinctive, bold taste of Dr Pepper has been popular ever since.");